targets.expr:
(
  sum(
    node_filesystem_size_bytes{
      instance="$node",
      job="$job",
      mountpoint=~"^/nas(/.*)?$",
      fstype!="rootfs"
    }
  )
  -
  sum(
    node_filesystem_avail_bytes{
      instance="$node",
      job="$job",
      mountpoint=~"^/nas(/.*)?$",
      fstype!="rootfs"
    }
  )
)
/
sum(
  node_filesystem_size_bytes{
    instance="$node",
    job="$job",
    mountpoint=~"^/nas(/.*)?$",
    fstype!="rootfs"
  }
)
* 100
